,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE',
'    i_department_id NUMBER := TO_NUMBER(APEX_APPLICATION.G_X01);',
'    v_count NUMBER;',
'BEGIN',
'',
'    SELECT COUNT(*) INTO v_count FROM DEPARTMENTS WHERE ID = i_department_id;',
'',
'    IF v_count = 0 THEN',
'        APEX_JSON.OPEN_OBJECT;',
'        APEX_JSON.WRITE(''status'', ''error'');',
'        APEX_JSON.WRITE(''message'', ''Department not found.'');',
'        APEX_JSON.CLOSE_OBJECT;',
'        RETURN;',
'    END IF;',
'',
'    UPDATE EMPLOYEES',
'    SET DEPARTMENT_ID = NULL',
'    WHERE DEPARTMENT_ID = i_department_id;',
