'    DELETE FROM DEPARTMENTS WHERE ID = i_department_id;',
'',
'    COMMIT;',
'',
'    APEX_JSON.OPEN_OBJECT;',
'    APEX_JSON.WRITE(''status'', ''success'');',
'    APEX_JSON.CLOSE_OBJECT;',
'',
'EXCEPTION',
'    WHEN OTHERS THEN',
'        ROLLBACK;',
'        APEX_JSON.OPEN_OBJECT;',
'        APEX_JSON.WRITE(''status'', ''error'');',
'        APEX_JSON.WRITE(''message'', SQLERRM);',
'        APEX_JSON.CLOSE_OBJECT;',
'END;'))
